import { Table, Image, InputNumber, Button, Select } from "antd";
import React, { useState } from "react";
import { DeleteOutlined } from "@ant-design/icons";

const { Option } = Select;

export default function ShoppingCartList(props) {
    const columns = [
        {
            title: "Hình Ảnh",
            dataIndex: "image",
            render: (image) => (
                <Image
                    width={100}
                    height={100}
                    src={image}
                    fallback="data:image/png;base64,"
                />
            ),
        },
        {
            title: "Sản Phẩm",
            dataIndex: "name",
        },
        {
            title: "Màu Sắc",
            dataIndex: "color",
            render: (_, record) => (
                <Select width={50}>
                    {record.color.map((color, index) => (
                        <Option
                            key={index}
                            value={color.hex}
                            onChange={(e) => onUpdateProductColor(e, record)}
                        >
                            <div
                                className="h-4 w-4"
                                style={{ backgroundColor: color.hex }}
                            ></div>
                        </Option>
                    ))}
                </Select>
            ),
        },
        {
            title: "Số Lượng",
            dataIndex: "quantity",
            render: (_, record) => (
                <InputNumber
                    min={1}
                    max={10}
                    value={record.quantity}
                    onChange={(e) => onUpdateProductPrice(e, record)}
                    width={50}
                />
            ),
        },

        {
            title: "Thành Tiền",
            dataIndex: "price",
            render: (_, record) => (
                <span>{record.price * record.quantity}</span>
            ),
        },

        {
            title: "Xóa",
            dataIndex: "delete",
            render: (_, record) => (
                <Button
                    type="danger"
                    shape="circle"
                    icon={<DeleteOutlined />}
                    onClick={() => onDeleteProductFromCart(record)}
                />
            ),
        },
    ];

    const onDeleteProductFromCart = (record) => {
        setShoppingCartData((shoppingCartData) => {
            const newData = shoppingCartData.filter(
                (product) => product.id !== record.id
            );
            setShoppingCartData(newData);
        });
    };

    const onUpdateProductPrice = (e, record) => {
        console.log("event", e);
        console.log("record", record);
        setShoppingCartData((shoppingCartData) => {
            const newData = shoppingCartData.map((product) => {
                if (product.id === record.id) {
                    product.quantity = e;
                }
                return product;
            });
            setShoppingCartData(newData);
        });
        console.log("shoppingCartData", shoppingCartData);
    };

    const onUpdateProductColor = (e, record) => {
        console.log("event", e);
        console.log("record", record);
        setShoppingCartData((shoppingCartData) => {
            const newData = shoppingCartData.map((product) => {
                if (product.id === record.id) {
                    product.color = e;
                }
                return product;
            });
            setShoppingCartData(newData);
        });
        console.log("shoppingCartData", shoppingCartData);
    };

    const [shoppingCartData, setShoppingCartData] = useState(props.data);

    const countTotalPrice = () => {
        let totalPrice = 0;
        shoppingCartData.forEach((product) => {
            totalPrice += product.price * product.quantity;
        });
        return totalPrice;
    };

    return (
        <div>
            <Table
                columns={columns}
                dataSource={shoppingCartData}
                pagination={false}
            />
            <div>
                <span>Tổng Tiền: </span>
                <span>{countTotalPrice()}</span>
            </div>
        </div>
    );
}
